use quantum;
select concat('"<tab>',t1.code,'"') as code,t1.name,t2.curr_price,t1.deal_price,t1.count as 'volume(tens of thousands)',t1.deal_price*t1.count as 'amount',(t1.deal_price-t2.curr_price)/t1.deal_price as 'premium_ratio((deal-curr)/deal)',buy_dealer,sold_dealer,t1.p_date from (select * from dazong_general where str_to_date('20160110','%Y%m%d')<=p_date and (buy_dealer='机构专用' or sold_dealer='机构专用' or buy_dealer like '%交易单元%' or sold_dealer like '%交易单元%' or buy_dealer like '%总部%' or sold_dealer like '%总部%')) t1 join (select code,curr_price from prices) t2 on t1.code=t2.code order by p_date desc,7 desc,6 desc;
